import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import { callApi } from '../util';


const MessageForm = ({post, token, fetchPosts}) => {
    console.log('post', post)
    const { postId } = useParams();
    const [content, setContent] = useState('');
    const handleSubmit = async (ev) => {
        ev.preventDefault();
        const data = await callApi({
            method: 'POST',
            url: `/posts/${post._id}/messages`,
            token,
            body: {
                message: {
                    content
                }
            }
        });
        const messageResp = await callApi({url: `/posts`, token});
        console.log('messageResp', messageResp);
        setContent(messageResp.data.posts.messages)
        // setContent(messageResp.data.post.messages);
        // await fetchPosts();
    }
    return <>
        <form onSubmit={handleSubmit}>
            <input value={content} placeholder="content" onChange={(ev) => setContent(ev.target.value)}></input>
            <button type="submit">Send Message</button>
        </form>
    </>
}

export default MessageForm;
