import React from 'react';
import { useParams } from 'react-router-dom';

import PostSingle from './PostSingle';
import MessageForm from './MessageForm';

const PostView = ({posts, token}) => {
    console.log('posts',posts)
    const { postId } = useParams();
    console.log('postId', postId);
    const postResp = posts.filter(post => post._id === Number(postId));
    console.log('postResp', postResp)
    return <>
        <PostSingle post={post} >
            {
                postResp && postResp.messages && postResp.message.map(mess => <div key={mess._id}>Message: {mess.content}</div>)
            }
            <MessageForm post={post} token={token} />
        </PostSingle>
    </>
}

export default PostView;
