import { callApi } from '../util';

const MessageForm = ({post, token}) => {
    const [content, setContent] = useState('');
    const handleSubmit = async (ev) => {
        ev.preventDefault();
        const data = await callApi({
            method: 'POST',
            url: `/posts/${postId}/messages`,
            token,
            body: {
                message: {
                    content
                }
            }
        });
        const messageResp = await callApi({url: '/posts', token});
        setContent(messageResp.data.posts);
    }
    return <>
        <div>Message</div>
    </>
}

export default MessageForm;
