import React from 'react';
import { callApi } from '../util';
import PostSingle from './PostSingle';

const EditPost = ({ posts, token, fetchPosts}) => {
    console.log('postsin edit', posts)

    const handleEdit = async (postId) => {
        const editPost = callApi({
            method: 'PATCH',
            url: `/posts/${postId}`,
            body: {
                post: {
                    title,
                    price, 
                    description,
                    location
                }},
            token
        });
        await fetchPosts();
    }
    return <>
    <div>Edit</div>
    {
        posts.map(post => <PostSingle key={post._id} post={post} token={token}>
            {
                token && posts.isAuthor && <button onClick={() => handleEdit(post._id)}>Edit Post</button>
            }
        </PostSingle>)
    }

    </>
}

export default EditPost;
