import React from 'react';
import { callApi } from '../util';
import AddPost from './AddPosts';
import PostSingle from './PostSingle';

const EditPost = ({ posts, token, fetchPosts}) => {
    console.log('postsin edit', posts)

    const handleEdit = async (postId) => {
        const editPost = callApi({
            method: 'PATCH',
            url: `/posts/${postId}`,
            body: {
                post: {
                    title,
                    price, 
                    description,
                    location,
                    willDeliver
                }},
            token
        });
        console.log(editPost)
        await fetchPosts();

    }
    return <>
    {
        posts.map(post => <AddPost key={post._id} post={post} token={token}>
            {
                posts.isAuthor ? <button onClick={() => handleEdit(post._id)}>Edit Post</button> : ''
            }
        </AddPost>)
    }

    </>
}

export default EditPost;
