        posts.map(post => <PostSingle key={post._id} token={token} post={post}>
            {
                post.isAuthor && <button onClick={() => handleUpdate(post._id)}>Update Post</button>
            }
        </PostSingle>)
    } */}
    </>
}

export default AddPost;
